mapper.select:
select a.* from appraisal_detail a,collect b

  a.id = b.collect_id

    and b.user_id = #{userId}


  and b.collect_type = #{collectType}


order by b.add_time desc

  limit #{start},#{limit}

select a.*,b.nick_name,b.head_img_url from appraisal_detail a,user_info b where a.user_id=b.id
and a.user_id = #{userId}
and a.title like "%"#{keyword}"%"
limit #{start},#{limit}
