mapper.insert:
insert into appraisal_detail (id, user_id, title, 
  bonus, label, sort, 
  status, end_time, update_time, 
  add_time, pv_count, content
  )
values (#{id,jdbcType=INTEGER}, #{userId,jdbcType=INTEGER}, #{title,jdbcType=VARCHAR}, 
  #{bonus,jdbcType=DECIMAL}, #{label,jdbcType=VARCHAR}, #{sort,jdbcType=INTEGER}, 
  #{status,jdbcType=INTEGER}, #{endTime,jdbcType=TIMESTAMP}, #{updateTime,jdbcType=TIMESTAMP}, 
  #{addTime,jdbcType=TIMESTAMP}, #{pvCount,jdbcType=INTEGER}, #{content,jdbcType=LONGVARCHAR}
  )

mapper.select:
select a.*,c.nick_name,c.head_img_url from appraisal_detail a,collect b,user_info c

  a.id = b.collect_id and a.user_id = c.id and c.status  <>  3

    and b.user_id = #{userId}


    and b.collect_type = #{collectType}


    and (a.title like "%"#{search}"%" or a.content like "%"#{search}"%" or a.user_id in (select id from user_info where nick_name like "%"#{search}"%" and status  <>  3))


order by b.add_time desc

  limit #{start},#{limit}

select a.*,b.nick_name,b.head_img_url from appraisal_detail a,user_info b where a.user_id=b.id and b.status <> 3
and a.user_id = #{userId}
and a.title like "%"#{keyword}"%"
order by a.add_time desc limit #{start},#{limit}

select a.*,b.nick_name,b.head_img_url from appraisal_detail a,user_info b,appraisal_comment c where a.user_id=b.id and a.id = c.appraisal_id and b.status <> 3
and c.user_id = #{userId}
and (a.title like "%"#{keyword}"%" or a.content like "%"#{keyword}"%" or b.nick_name like "%"#{keyword}"%" or c.content like "%"#{keyword}"%")
order by a.add_time desc limit #{start},#{limit}
