mapper.select:
select a.* from forum_column_apply a where a.status = #{status}

   and a.column_name like "%"#{keyword}"%" or (select nick_name from user_info where id = a.user_id and status  <>  3) like "%"#{keyword}"%"

 order by a.add_time desc limit #{start},#{limit}

select count(*) from forum_column_apply where 1=1

  and ( 
  	column_name like "%"#{keyword,jdbcType=VARCHAR}"%"
  )


  and status = #{status}


  and add_time  >  #{startDate}



  and add_time  <  #{endDate}

select a.*,b.nick_name,b.head_img_url,b.phone,c.name from forum_column_apply a,user_info b,forum_column_type c where a.user_id=b.id and a.type_id=c.id

  and ( 
  	a.column_name like "%"#{keyword,jdbcType=VARCHAR}"%"
  	and b.nick_name like %"#{keyword,jdbcType=VARCHAR}"%"
  	and b.phone like %"#{keyword,jdbcType=VARCHAR}"%"
  )


  and a.status = #{status}



  and a.add_time  >  #{startDate}



  and a.add_time  <=  #{endDate}



  order by ${orders}

    desc


limit #{start},#{limit}
