mapper.select:
select 
a.*,b.name as type_name
from forum_column a,forum_column_type b where a.type_id=b.id order by b.sort limit #{start},#{limit}

select a.* from forum_column a,collect b

  a.id = b.collect_id

    and b.user_id = #{userId}


    and b.collect_type = #{collectType}


order by b.add_time desc

  limit #{start},#{limit}
