mapper.select:
select
a.*,b.nick_name,b.head_img_url
from forum_comment a,user_info b
where a.user_id=b.id and forum_id=#{forumId}
order by id desc
limit #{start},#{limit}

select
a.*,b.nick_name,b.head_img_url
from forum_comment a,user_info b
where a.user_id=b.id and comment_id=#{commentId}
order by id desc
limit #{start},#{limit}
