mapper.select:
select
a.*,b.nick_name,b.head_img_url
from forum_info a,user_info b
where a.user_id=b.id and b.status  <>  3

  and a.column_id = #{columnId}


  and a.type = #{type}


  and a.user_id = #{userId}


  and (a.title like "%"#{keyword}"%" or a.content like "%"#{keyword}"%")


  and a.is_chosen = 1


  and (a.pv_count + (select count(*) from forum_pv where forum_id = a.id) >= 10000)

order by a.update_time desc
limit #{start},#{limit}

select a.*,c.nick_name,c.head_img_url from forum_info a,collect b,user_info c

  a.id = b.collect_id and a.user_id = c.id and c.status  <>  3

    and b.user_id = #{userId}


    and a.type = #{type}


    and b.collect_type = #{collectType}


    and (a.title like "%"#{search}"%" or a.content like "%"#{search}"%" or c.nick_name like "%"#{search}"%")


order by b.add_time desc

  limit #{start},#{limit}

select count(*) from forum_column where 1=1

  and ( 
  	name like "%"#{keyword,jdbcType=VARCHAR}"%"
  )


  and status = #{status}


  and add_time  >  #{startDate}



  and add_time  <=  #{endDate}

select a.*,b.head_img_url,b.nick_name from forum_column a,user_info b where a.user_id=b.id

  and (
    a.name like "%"#{keyword,jdbcType=VARCHAR}"%"
    or b.nick_name like "%"#{keyword,jdbcType=VARCHAR}"%"
  )


  and a.status = #{status}



  and a.add_time  >  #{startDate}



  and a.add_time  <=  #{endDate}



  order by ${orders}

limit #{start},#{limit}
