mapper.select:
select count(*) from report a,forum_info b

  a.business_id = b.id and (a.type = 2 or a.type = 3)

    and b.type = #{type}


    and a.status = #{status}


    and (
    b.title like "%"#{keyword}"%"
    or b.content like "%"#{keyword}"%"
    or a.user_id in (select id from user_info where nick_name like  "%"#{keyword}"%")
    or b.user_id in (select id from user_info where nick_name like  "%"#{keyword}"%")
    )


    and a.add_time  >  #{startDate}


    and a.add_time  <=  #{endDate}

select a.*,b.title name,c.nick_name,c.head_img_url from report a,forum_info b,user_info c

  a.business_id = b.id and a.user_id = c.id and (a.type = 2 or a.type = 3)

    and b.type = #{type}


    and a.status = #{status}


    and (
    b.title like "%"#{keyword}"%"
    or b.content like "%"#{keyword}"%"
    or a.user_id in (select id from user_info where nick_name like  "%"#{keyword}"%")
    or b.user_id in (select id from user_info where nick_name like  "%"#{keyword}"%")
    )


    and a.add_time  >  #{startDate}


    and a.add_time  <=  #{endDate}



  order by ${orders}

    desc



  limit #{start},#{limit}

select count(*) from report a,appraisal_detail b

  a.business_id = b.id and a.type = 4

    and a.status = #{status}


    and (
    b.title like "%"#{keyword}"%"
    or b.content like "%"#{keyword}"%"
