select a.*,b.title name,c.nick_name,c.head_img_url from report a,appraisal_detail b,user_info c

  a.business_id = b.id and a.user_id = c.id and a.type = 4

    and a.status = #{status}


    and (
    b.title like "%"#{keyword}"%"
