  1=1

    and status = #{status}


    and type = #{type}


    and title like "%"#{keyword}"%"


    and add_time  >  #{startDate}


    and add_time  <=  #{endDate}

select * from banner_index

  1=1

    and status = #{status}


    and type = #{type}


    and title like "%"#{keyword}"%"


    and add_time  >  #{startDate}


    and add_time  <=  #{endDate}



