mapper.select:
select * from goods_appraisal_detail

  user_id = #{userId}

    and status = #{status}


    and status  <>  4


    and order_id = #{orderId}


order by submit_time desc,add_time desc

  limit #{start},#{limit}

select * from goods_appraisal_detail

  1=1

    and order_id = #{orderId}


    and status = #{status}

select count(*) from goods_appraisal_detail

  status  <>  4

    and type = #{type}


    and status = #{status}


    and(description like "%"#{keyword}"%" or user_id in (select id from user_info where nick_name like "%"#{keyword}"%"))


    and add_time  >  #{startDate}


    and add_time  <=  #{endDate}

select a.*,b.nick_name,c.vip_level from goods_appraisal_detail a,user_info b,user_vip c

  a.status  <>  4 and a.user_id = b.id and a.user_id = c.user_id

    and a.type = #{type}


    and a.status = #{status}


    and(a.title like "%"#{keyword}"%" or a.description like "%"#{keyword}"%" or a.user_id in (select id from user_info where nick_name like "%"#{keyword}"%"))


    and a.add_time  >  #{startDate}


    and a.add_time  <=  #{endDate}




    order by c.${orders}


    order by a.${orders}


    desc



  limit #{start},#{limit}
