mapper.select:
select count(*) from report a,goods_info b

  a.business_id = b.id and a.type = 1

    and b.type = #{type}


    and a.status = #{status}


    and (
    b.name like "%"#{keyword}"%"
    or b.description like "%"#{keyword}"%"
    or a.user_id in (select id from user_info where nick_name like  "%"#{keyword}"%")
    or b.user_id in (select id from user_info where nick_name like  "%"#{keyword}"%")
    )


    and a.add_time  >  #{startDate}


    and a.add_time  <=  #{endDate}

select a.*,b.name goodsName,c.nick_name,c.head_img_url from report a,goods_info b,user_info c

  a.business_id = b.id and a.type = 1 and a.user_id = c.id

    and b.type = #{type}


    and a.status = #{status}


    and (
    b.name like "%"#{keyword}"%"
    or b.description like "%"#{keyword}"%"
    or a.user_id in (select id from user_info where nick_name like  "%"#{keyword}"%")
    or b.user_id in (select id from user_info where nick_name like  "%"#{keyword}"%")
    )


    and a.add_time  >  #{startDate}


    and a.add_time  <=  #{endDate}



  order by ${orders}

     desc



  limit #{start},#{limit}
