mapper.insert:
insert into order_info (id, user_id, main_order_id, 
  order_no, amount, count, 
  status, add_time, update_time, 
  seller_id, is_resolve, is_refund
  )
values (#{id,jdbcType=INTEGER}, #{userId,jdbcType=INTEGER}, #{mainOrderId,jdbcType=INTEGER}, 
  #{orderNo,jdbcType=VARCHAR}, #{amount,jdbcType=DECIMAL}, #{count,jdbcType=INTEGER}, 
  #{status,jdbcType=INTEGER}, #{addTime,jdbcType=TIMESTAMP}, #{updateTime,jdbcType=TIMESTAMP}, 
  #{sellerId,jdbcType=INTEGER}, #{isResolve,jdbcType=INTEGER}, #{isRefund,jdbcType=INTEGER}
  )

mapper.select:
select * from order_info

  status  <>  5 and status  <>  6 and is_resolve = 2

    and user_id = #{userId}


    and main_order_id = #{mainOrderId}

select a.* from order_info a,order_detail b

  a.id = b.order_id and a.status  <>  6 and a.is_resolve = 2

    and a.seller_id = #{sellerId}


    and a.status = #{status}


    and b.goods_id in (select id from goods_info where name like "%"#{search}"%" or description like "%"#{search}"%")


order by a.add_time desc limit #{start},#{limit}

select * from order_info

  status  <>  6 and is_resolve = 2

    and user_id = #{userId}


    and id = #{orderId}

select * from order_info

  is_refund = 1

    and user_id = #{userId}


    and seller_id = #{userId}


order by add_time desc

  limit #{start},#{limit}
