mapper.select:
select DISTINCT a.*,c.nick_name,c.head_img_url from order_info a,order_detail b,user_info c

  (a.id = b.order_id or a.id = b.main_order_id) and a.is_resolve = 2 and a.user_id = c.id and a.buyer_is_delete  <>  1

    and a.user_id = #{userId}


    and a.status = #{status}


    and a.is_refund = 1


    and b.goods_id in (select id from goods_info where name like "%"#{search}"%" or description like "%"#{search}"%")


order by a.add_time desc

  limit #{start},#{limit}

select a.*,b.nick_name,b.head_img_url from order_info a,user_info b

  a.status  <>  5 and a.user_id = b.id

    and a.user_id = #{userId}


    and a.main_order_id = #{mainOrderId}

select DISTINCT a.*,c.nick_name,c.head_img_url from order_info a,order_detail b,user_info c

  a.id = b.order_id and a.seller_is_delete  <>  1 and a.is_resolve = 2 and a.user_id = c.id

    and a.seller_id = #{sellerId}


    and a.status = #{status}


    and a.is_refund = 1


    and b.goods_id in (select id from goods_info where name like "%"#{search}"%" or description like "%"#{search}"%")


order by a.add_time desc

  limit #{start},#{limit}

select a.*,b.nick_name,b.head_img_url from order_info a,user_info b

  a.buyer_is_delete  <>  1 and a.is_resolve = 2 and a.user_id = b.id

    and a.user_id = #{userId}


    and a.id = #{orderId}

select a.*,c.nick_name,c.head_img_url from order_info a,order_refund b,user_info c

  a.is_refund = 1 and b.order_id = a.id and b.status = 1 and (a.status = 2 or a.status = 3) and a.user_id = c.id

    and a.user_id = #{userId}


    and a.seller_id = #{userId}


order by a.add_time desc

  limit #{start},#{limit}

select DISTINCT a.*,c.nick_name,c.head_img_url,c.phone from order_info a,order_detail b,user_info c

  (a.id = b.order_id or a.id = b.main_order_id) and a.is_resolve = 2 and a.user_id = c.id

    and (
      a.order_no like "%"#{keyword}"%"
      or b.goods_name like "%"#{keyword}"%"
      or c.nick_name like "%"#{keyword}"%"
    )



      and a.is_refund = 1


      and a.status = #{status} and a.is_refund = 2



    and a.add_time  >  #{startDate}


    and a.add_time  <=  #{endDate}



  order by a.${orders}

    desc



  limit #{start},#{limit}
