mapper.select:
select * from order_refund

  1=1

    and seller_return_status = #{sellerReturnStatus}


    and order_id = #{orderId}


    and status = #{status}

select count(*) from order_refund a,order_info b

  a.order_id = b.id



   and a.status = #{status}



    and a.add_time  >  #{startDate}


    and a.add_time  <=  #{endDate}

select *,b.order_no from order_refund a,order_info b

  a.order_id = b.id



   and a.status = #{status}


    and a.add_time  >  #{startDate}


    and a.add_time  <=  #{endDate}



  order by a.${orders}

    desc



  limit #{start},#{limit}
