mapper.insert:
insert into app_version (id, version_no, version_desc, 
  version_name, version_url, package_name, 
  type, add_time, update_time
  )
values (#{id,jdbcType=INTEGER}, #{versionNo,jdbcType=INTEGER}, #{versionDesc,jdbcType=VARCHAR}, 
  #{versionName,jdbcType=VARCHAR}, #{versionUrl,jdbcType=VARCHAR}, #{packageName,jdbcType=VARCHAR}, 
  #{type,jdbcType=INTEGER}, #{addTime,jdbcType=TIMESTAMP}, #{updateTime,jdbcType=TIMESTAMP}
  )

mapper.select:
select count(*) from app_version where 1=1

  and (
  version_desc like "%"#{keyword,jdbcType=VARCHAR}"%"
  or version_name like "%"#{keyword,jdbcType=VARCHAR}"%"
  or package_name like "%"#{keyword,jdbcType=VARCHAR}"%"
  )


  and add_time  >  #{startDate}


  and add_time  <=  #{endDate}

select * from app_version

  1=1

    and (
    version_desc like "%"#{keyword,jdbcType=VARCHAR}"%"
    or version_name like "%"#{keyword,jdbcType=VARCHAR}"%"
    or package_name like "%"#{keyword,jdbcType=VARCHAR}"%"
    )


    and add_time  >  #{startDate}


    and add_time  <=  #{endDate}



  order by ${orders}

    desc



  limit #{start},#{limit}
