mapper.select:
select count(*) from identity_auth_apply a,user_info b where a.user_id=b.id

  and (
  b.phone like "%"#{keyword,jdbcType=VARCHAR}"%"
  or b.nick_name like "%"#{keyword,jdbcType=VARCHAR}"%"
  )


  and a.status = #{status}


    and a.add_time  >  #{startDate}



    and a.add_time  <=  #{endDate}

select a.*,b.nick_name,b.phone from identity_auth_apply a,user_info b where a.user_id=b.id

  and (
  b.phone like "%"#{keyword,jdbcType=VARCHAR}"%"
  or b.nick_name like "%"#{keyword,jdbcType=VARCHAR}"%"
  or a.identity like "%"#{keyword,jdbcType=VARCHAR}"%"
  or a.name like "%"#{keyword,jdbcType=VARCHAR}"%"
  )


  and a.status = #{status}



  and a.add_time  >  #{startDate}



  and a.add_time  <=  #{endDate}



  order by a.${orders}

    desc


limit #{start},#{limit}
