mapper.select:
select count(*) from user_auth_apply a,user_info_ext b where a.user_id=b.id

  and ( 
    b.name like "%"#{keyword,jdbcType=VARCHAR}"%"
    or b.identity like "%"#{keyword,jdbcType=VARCHAR}"%"
  )


  and a.status = #{status}


  and a.add_time  >  #{startDate}



  and a.add_time  <=  #{endDate}

select a.* from user_auth_apply a,user_info b where a.user_id=b.id

  and (
    b.nick_name like "%"#{keyword,jdbcType=VARCHAR}"%"
  )


  and a.status = #{status}



  and a.add_time  >  #{startDate}



  and a.add_time  <=  #{endDate}



  order by a.${orders}

    desc


limit #{start},#{limit}
