mapper.select:
select count(*) from platform_finance_detail a,user_info b where a.user_id=b.id

    and a.pay_type = #{payType}


    and a.type = #{type}


    and a.order_type = #{orderType}


    and (
    a.title like "%"#{keyword}"%"
    or b.nick_name like "%"#{keyword}"%"
    or b.phone like "%"#{keyword}"%"
    )


    and
    ((
    a.pay_type  <>  3
    ) or (
    a.order_type = 8 and a.type = 2
    ))


    and(
    a.order_type = 2
    or a.order_type = 5
    or a.order_type = 8
    or a.order_type = 9
    or a.order_type = 10
    )


    and a.add_time  >  #{startDate}


    and a.add_time  <=  #{endDate}

select a.*,b.nick_name from platform_finance_detail a,user_info b where a.user_id=b.id

    and a.pay_type = #{payType}


    and a.type = #{type}


    and a.order_type = #{orderType}


    and (
    a.title like "%"#{keyword}"%"
    or b.nick_name like "%"#{keyword}"%"
    or b.phone like "%"#{keyword}"%"
    )


    and
    ((
      a.pay_type  <>  3
    ) or (
      a.order_type = 8 and a.type = 2
    ))


    and(
    a.order_type = 2
    or a.order_type = 5
    or a.order_type = 8
    or a.order_type = 9
    or a.order_type = 10
    )



    and a.add_time  >  #{startDate}



    and a.add_time  <=  #{endDate}



    order by a.${orders}

        desc


limit #{start},#{limit}
