mapper.select:
select count(*) from user_vip a,user_info b

  a.user_id = b.id and a.status = 1

    and (b.nick_name like "%"#{keyword}"%"
    or b.phone like "%"#{keyword}"%"
    or a.vip_no like "%"#{keyword}"%")


    and a.vip_level = #{vipLevel}


    and a.vip_type = #{vipType}


    and a.is_send_message = #{isSendMessage}


    and a.add_time  >  #{startDate}


    and a.add_time  <=  #{endDate}

select a.*,b.nick_name,b.head_img_url from user_vip a,user_info b

  a.user_id = b.id and a.status = 1

    and (b.nick_name like "%"#{keyword}"%"
    or b.phone like "%"#{keyword}"%"
    or a.vip_no like "%"#{keyword}"%")


    and a.vip_level = #{vipLevel}


    and a.vip_type = #{vipType}


    and a.is_send_message = #{isSendMessage}


    and a.add_time  >  #{startDate}


    and a.add_time  <=  #{endDate}



  order by a.${orders}

    desc



  limit #{start},#{limit}
